'use client';

import Image from 'next/image';
import { useState } from 'react';

interface OptimizedImageProps {
  src: string;
  alt: string;
  width?: number;
  height?: number;
  className?: string;
  priority?: boolean;
  sizes?: string;
}

export default function OptimizedImage({
  src,
  alt,
  width,
  height,
  className,
  priority = false,
  sizes = "(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 33vw"
}: OptimizedImageProps) {
  const [isLoading, setIsLoading] = useState(true);
  const [hasError, setHasError] = useState(false);

  return (
    <div className={`${className} ${isLoading ? 'animate-pulse bg-gray-800' : ''}`}>
      <Image
        src={src}
        alt={alt}
        width={width}
        height={height}
        sizes={sizes}
        priority={priority}
        quality={85} // Balanced quality for mobile
        placeholder="blur"
        blurDataURL="data:image/jpeg;base64,"
        onLoad={() => setIsLoading(false)}
        onError={() => {
          setHasError(true);
          setIsLoading(false);
        }}
        style={{
          maxWidth: '100%',
          height: 'auto',
        }}
      />
      {hasError && (
        <div className="flex items-center justify-center bg-gray-800 text-gray-400 p-4">
          Failed to load image
        </div>
      )}
    </div>
  );
}
